const batchData = $input.first().json;
const batch = batchData.batch;
const spec = batchData.spec;
const context = batchData.execution_context;

const sections = [];

for (let i = 0; i < batch.blueprints.length; i++) {
  const blueprint = batch.blueprints[i];
  const globalIndex = batch.start_index + i;

  const recentSections = sections.slice(-2);
  const contextSummary = recentSections.length > 0
    ? recentSections.map(s => `[${s.p_id}] ${s.text.substring(0, 100)}...`).join('\n')
    : "è¿™æ˜¯æ–‡ç« çš„å¼€ç¯‡éƒ¨åˆ†";

  const remainingSections = spec.blueprint.length - globalIndex;
  const remainingWords = context.avg_words_per_section * remainingSections;

  // Parse word_count_target from string like "200Â±20å­—" to number
  let wordCountTarget = 200; // default
  if (blueprint.word_count_target) {
    const match = String(blueprint.word_count_target).match(/(\d+)/);
    if (match) {
      wordCountTarget = parseInt(match[1], 10);
    }
  }

  const sectionContext = {
    blueprint: {
      ...blueprint,
      word_count_target: wordCountTarget,
      word_count_target_raw: blueprint.word_count_target
    },
    progress: {
      current: globalIndex + 1,
      total: spec.blueprint.length,
      remaining_sections: remainingSections,
      remaining_words: remainingWords
    },
    previous_context: contextSummary,
    global_spec: {
      identity: spec.identity,
      constraints: spec.constraints,
      lexical: spec.lexical,
      rhetoric: spec.rhetoric,
      golden_sample: spec.golden_sample
    },
    meta: spec.meta,
    search_material: context.search_material
  };

  sections.push({
    p_id: blueprint.p_id,
    context: sectionContext,
    text: "",
    index: globalIndex
  });
}

return sections.map(s => ({ json: s }));
