 */

// 1. è·å–è¾“å…¥æ–‡æœ¬ (å…¼å®¹ text, message, output ç­‰å­—æ®µ)
const rawInput = $input.first().json.text || $input.first().json.output || $input.first().json.message || "";

function robustParse(text) {
    if (!text) return { error: "INPUT_EMPTY" };

    // 2. æ¸…æ´— Markdown æ ‡ç­¾å¹¶æå– JSON ä¸»ä½“
    // å¯»æ‰¾ç¬¬ä¸€ä¸ª [ å’Œæœ€åä¸€ä¸ª ] ä¹‹é—´çš„å†…å®¹
    let jsonBody = "";
    const startIdx = text.indexOf('[');
    const endIdx = text.lastIndexOf(']');

    if (startIdx === -1) {
        return { error: "NO_ARRAY_FOUND" };
    }

    // å¦‚æœæ²¡æœ‰æ‰¾åˆ°ç»“æŸç¬¦ï¼Œè¯´æ˜è¢«æˆªæ–­äº†ï¼Œå°è¯•è¡¥é½
    if (endIdx === -1 || endIdx < startIdx) {
        jsonBody = text.substring(startIdx).trim();
    } else {
        jsonBody = text.substring(startIdx, endIdx + 1).trim();
    }

    // 3. åŸºç¡€æ¸…æ´—ï¼šç§»é™¤å¯èƒ½å¯¼è‡´è§£æå¤±è´¥çš„æ§åˆ¶å­—ç¬¦
    jsonBody = jsonBody.replace(/[\u0000-\u001F\u007F-\u009F]/g, "");

    try {
        // å°è¯•åˆæ¬¡è§£æ
        return JSON.parse(jsonBody);
    } catch (e) {
        // 4. æ·±åº¦ä¿®å¤é€»è¾‘ï¼šå¤„ç†æˆªæ–­å¯¼è‡´çš„æ— æ•ˆæœ«å°¾
        let repaired = jsonBody;

        // ç§»é™¤æœ«å°¾å¤šä½™çš„é€—å·ã€åæ–œæ æˆ–ä¸å®Œæ•´çš„é”®å€¼å¯¹
        repaired = repaired.replace(/,\s*$/, "").replace(/\\$/, "");

        // è¡¥é½æœªé—­åˆçš„å¼•å·
        const quoteCount = (repaired.match(/"/g) || []).length;
        if (quoteCount % 2 !== 0) repaired += '"';

        // è¡¥é½æœªé—­åˆçš„æ‹¬å· (é’ˆå¯¹å¯¹è±¡ {} å’Œæ•°ç»„ [])
        const openBraces = (repaired.match(/\{/g) || []).length;
        const closeBraces = (repaired.match(/\}/g) || []).length;
        const openBrackets = (repaired.match(/\[/g) || []).length;
        const closeBrackets = (repaired.match(/\]/g) || []).length;

        for (let i = 0; i < (openBraces - closeBraces); i++) repaired += "}";
        for (let i = 0; i < (openBrackets - closeBrackets); i++) repaired += "]";

        try {
            return JSON.parse(repaired);
        } catch (innerError) {
            // æœ€åçš„æŒ£æ‰ï¼šé€šè¿‡æˆªæ–­åˆ°æœ€åä¸€ä¸ªå®Œæ•´å¯¹è±¡æ¥ä¿ä½æ•°æ®
            const lastValidObject = repaired.lastIndexOf('}');
            if (lastValidObject !== -1) {
                try {
                    let finalTry = repaired.substring(0, lastValidObject + 1);
                    if (!finalTry.endsWith(']')) finalTry += ']';
                    return JSON.parse(finalTry);
                } catch (f) {
                    return { error: "JSON_PARSE_FAILED", detail: innerError.message };
                }
            }
            return { error: "STRUCTURAL_ERROR", detail: innerError.message };
        }
    }
}

const parsedResult = robustParse(rawInput);

